function Irel = MSC(yd)% MSC  Function to calculate the theoretical%      diffraction pattern from a number (N) of slits.%      The relevant equations are:%      I(theta) = I(0) (sin(beta)/beta)^2 %                    * (sin(N.alpha)/N.sin(alpha))^2%      where%        alpha = pi*slitSepn*sin(theta)/lambda,%        beta  = pi*slitWdth*sin(theta)/lambda,%        tan(theta) = yd / scrnDist.%     % -----------------------------------------------% Set up the optical and mathematical variables % -----------------------------------------------% All optical variables are global and set elsewhereglobal lambda scrnDist scrnWdth slitWdth slitSepn numSlits;  % First calculate theta, beta and phi  theta = 1;  beta  = 1;  phi   = 1;% First calculate the diffraction pattern from a single slit  slitPatn = 1;  % Then calculate the diffraction pattern from N point sources  srcePatn = 1;  % Last multiply the two to produce the relative intensity  Irel = slitPatn .* srcePatn ;  return;